query:
mutation AddRecipe($recipe: RecipeInput!) {
  addRecipe(recipe: $recipe) {
    success
    message
    recipe {
      id
      title
      description
      source {
        display
        url
        __typename
      }
      photo
      servings
      directions {
        label
        steps {
          text
          __typename
        }
        __typename
      }
      ingredients {
        label
        ingredients {
          rangedAmount {
            min
            max
            __typename
          }
          amount
          unit
          prep
          name
          toTaste
          optional
          __typename
        }
        __typename
      }
      timing {
        prep {
          value
          units
          __typename
        }
        total {
          value
          units
          __typename
        }
        __typename
      }
      tags {
        id
        label
        type {
          id
          label
          __typename
        }
        __typename
      }
      meta {
        dateAdded
        dateUpdated
        __typename
      }
      __typename
    }
    __typename
  }
}
